import React from 'react';
import styled from "styled-components";
import {FlexWrapper} from "../../components/FlexWrapper";
import {SectionTitle} from "../../components/SectionTitle";
import {Button} from "../../components/Button";
import aboutImage1 from "../../assets/images/about/about-image-1.png"
import aboutImage2 from "../../assets/images/about/about-image-2.png"

export const About = () => {
    return (
        <StyledAbout>
            <FlexWrapper justify={"space-between"} align={"center"}>
                <Info>
                    <SectionTitle>
                        Create And Sell
                        Your Best NFTs
                    </SectionTitle>
                    <Text>
                        Start exploring the world of digital art and NFTs today and take control of your digital assets
                        with confidence!
                    </Text>
                    <FlexWrapper align={"center"} gap={"36px"}>
                        <Button>Create Now</Button>
                        <Button>Learn More</Button>
                    </FlexWrapper>
                </Info>
                <Images>
                    <Image src={aboutImage1} alt=""/>
                    <Image src={aboutImage2} alt=""/>
                </Images>
            </FlexWrapper>
        </StyledAbout>
    );
};

const StyledAbout = styled.section`
  min-height: 90vh;
  background-color: #202323;
`

const Info = styled.div`
  max-width: 426px;
`

const Images = styled.div`
  position: relative;
  width: 740px;
  height: 559px;
`
const Text = styled.p`

`
const Image = styled.img`
  border-radius: 24px;

  &:first-child {
    position: absolute;
    left: 0;
    bottom: 0;
    z-index: 1;
  }

  &:last-child {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 0;
  }
`
